  describe('+ GET: /connect', () => {
    it('+ Fails with no "Authorization" header field', function () {
      return new Promise((done) => {
        this.timeout(5000);
        request.get('/connect')
          .expect(401)
          .end((err, res) => {
            if (err) {
              return done(err);
            }
            expect(res.body).to.deep.eql({ error: 'Unauthorized' });
            done();
